mapper.insert:

	SELECT 'LESSON_INFO_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(LESSON_INFO_CODE, 13))), 0) + 1, 3, 0) 
	FROM LESSON_INFO


INSERT INTO LESSON_INFO(
	LESSON_INFO_CODE
	, SUBJECT_CODE
	, STEP_CODE
	, LESSON_DAY_CODE
	, LESSON_ROOM_CODE
	, TEACHER_CODE 
	, LESSON_TIME 
	, YEAR
	, MAX_STUDENT
	, NOW_STUDENT_CNT
	, MONEY
	, MEMO
) VALUES (
	#{lessonInfoCode}
	, #{subjectCode}
	, #{stepCode}
	, #{lessonDayCode}
	, #{lessonRoomCode}
	, #{teacherCode}
	, #{lessonTime}
	, #{year}
	, #{maxStudent}
	, 0
	, #{money}
	, #{memo}

)

mapper.select:
SELECT LESSON_INFO_CODE , TO_CHAR(LESSON_INFO_REGDATE, 'YYYY-MM-DD') AS LESSON_INFO_REGDATE , (SELECT SUBJECT_NAME FROM SUBJECT WHERE SUBJECT_CODE = L.SUBJECT_CODE) AS SUBJECT_NAME , (SELECT STEP_NAME FROM STEP WHERE STEP_CODE = L.STEP_CODE) AS STEP_NAME , YEAR , NOW_STUDENT_CNT , MAX_STUDENT , MONEY , LESSON_DAY_CODE , LESSON_TIME , (SELECT ROOM_NAME FROM LESSON_ROOM WHERE LESSON_ROOM_CODE = L.LESSON_ROOM_CODE) AS ROOM_NAME , (SELECT TEACHER_NAME FROM TEACHER WHERE TEACHER_CODE = L.TEACHER_CODE) AS TEACHER_NAME , MEMO , LESSON_INFO_STATUS FROM LESSON_INFO L
WHERE 1 = 1
AND L.SUBJECT_CODE = #{subjectCode}
AND L.YEAR = #{year}
AND L.STEP_CODE = #{stepCode}
AND LESSON_INFO_CODE = #{lessonInfoCode}

mapper.update:
 UPDATE LESSON_INFO
SET NOW_STUDENT_CNT = (SELECT MAX(NOW_STUDENT_CNT)+1 FROM LESSON_INFO WHERE LESSON_INFO_CODE = #{lessonInfoCode})
WHERE LESSON_INFO_CODE = #{lessonInfoCode}
