mapper.insert:

   SELECT 'LIKE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(LIKE_CODE, 6))),0) +1, 3, 0) 
   FROM LIKE_TABLE


INSERT INTO LIKE_TABLE (
	LIKE_CODE
	, TEACHER_ID
	, BOARD_NUM
) VALUES (
	#{likeCode}
	, #{teacherId}
	, #{boardNum}
)

mapper.delete:
DELETE FROM LIKE_TABLE
WHERE BOARD_NUM = #{boardNum}
AND TEACHER_ID = #{teacherId}

mapper.update:
UPDATE LIKE_TABLE 
SET LIKE_CHECK = 1
WHERE BOARD_NUM = #{boardNum}
AND TEACHER_ID = #{teacherId}

UPDATE LIKE_TABLE
SET LIKE_CHECK = 0
WHERE BOARD_NUM = #{boardNum}
AND TEACHER_ID = #{teacherId}

mapper.select:
SELECT COUNT(*) FROM LIKE_TABLE 
WHERE BOARD_NUM = #{boardNum}
AND TEACHER_ID = #{teacherId}
