mapper.select:
SELECT STUDENT_YEAR||'학년' AS STUDENT_YEAR
		, (SELECT ROUND(AVG(SCORE),2)
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND T.STUDENT_CODE = S.STUDENT_CODE
	AND SUBJECT_CODE =#{subjectCode}
	AND STUDENT_YEAR = 1) AS SCORE_AVG
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND SUBJECT_CODE = #{subjectCode}
	AND T.STUDENT_CODE = S.STUDENT_CODE
	AND STUDENT_YEAR = 1
UNION ALL
SELECT STUDENT_YEAR||'학년' AS STUDENT_YEAR
		, (SELECT ROUND(AVG(SCORE),2)
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND T.STUDENT_CODE = S.STUDENT_CODE
	AND SUBJECT_CODE =#{subjectCode}
	AND STUDENT_YEAR = 2) AS SCORE_AVG
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND SUBJECT_CODE =#{subjectCode}
	AND T.STUDENT_CODE = S.STUDENT_CODE
	AND STUDENT_YEAR = 2
UNION ALL
SELECT STUDENT_YEAR||'학년' AS STUDENT_YEAR
		, (SELECT ROUND(AVG(SCORE),2)
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND T.STUDENT_CODE = S.STUDENT_CODE
	AND SUBJECT_CODE =#{subjectCode}
	AND STUDENT_YEAR = 3) AS SCORE_AVG
	FROM TEST T, STUDENT S
	WHERE TEST_DATE = #{testDate}
	AND SUBJECT_CODE =#{subjectCode}
	AND T.STUDENT_CODE =S .STUDENT_CODE
	AND STUDENT_YEAR = 3
GROUP BY STUDENT_YEAR

SELECT COUNT(TEACHER_CODE)
FROM LESSON_INFO
WHERE TEACHER_CODE= #{teacherCode}
