mapper.insert:
INSERT INTO STUDENT (
	STUDENT_CODE
	, STUDENT_NAME
	, STUDENT_GENDER
	, BIRTHDAY
	, STUDENT_YEAR
	, ADDR
	, ADDR_DETAIL
	, STUDENT_TELL		
) VALUES (
	#{studentCode}
	, #{studentName}
	, #{studentGender}
	, #{birthday}
	, #{studentYear}
	, #{addr}
	, #{addrDetail}
	, #{studentTell}
)

mapper.update:
UPDATE STUDENT
SET STUDENT_TELL = #{studentTell}
	, ADDR = #{addr}
	, ADDR_DETAIL = #{addrDetail}

WHERE STUDENT_CODE = #{studentCode}

mapper.insert:

   SELECT 'STU_LESSON_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(STUDENT_LESSON_CODE, 12))),0) +1, 3, 0) 
   FROM STUDENT_LESSON_INFO


INSERT INTO STUDENT_LESSON_INFO(
	STUDENT_LESSON_CODE
	, STUDENT_CODE
	, LESSON_INFO_CODE
) VALUES (
	#{studentLessonCode}
	, #{studentCode}
	, #{lessonInfoCode}
)

mapper.select:
SELECT LESSON_INFO_CODE 
FROM STUDENT_LESSON_INFO
WHERE STUDENT_CODE = #{studentCode}

mapper.insert:


   SELECT 'PAYMENT_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PAYMENT_CODE, 9))),0) +1, 3, 0) 
   FROM PAYMENT


INSERT INTO PAYMENT(
	PAYMENT_CODE
	, STUDENT_LESSON_CODE
) VALUES (
	#{paymentCode}
	, (SELECT STUDENT_LESSON_CODE 
		FROM STUDENT_LESSON_INFO 
		WHERE STUDENT_CODE = #{studentCode}
		AND LESSON_INFO_CODE = #{lessonInfoCode})
)
