mapper.insert:

         SELECT 'TEST_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TEST_CODE, 6))), 0) + 1, 3, 0) 
         FROM TEST


INSERT INTO TEST (
	TEST_CODE
	, TEST_DATE
	, SCORE
	, SUBJECT_CODE
	, STUDENT_CODE
) VALUES(
	#{testCode}
	, TO_DATE(#{testDate} , 'yyyy/mm/dd') 
	, #{score}
	, #{subjectCode}
	, #{studentCode}
)

INSERT INTO TEST (
	TEST_CODE
