	, #{score}
	, #{subjectCode}
	, #{studentCode}
)

mapper.update:
UPDATE TEST
SET
SCORE = #{score}
WHERE TEST_CODE = #{testCode}

mapper.select:
 SELECT TEST_CODE
	, SUBJECT_CODE
	, STUDENT_CODE
	, TEST_DATE
	, SCORE
 FROM TEST
 WHERE 1=1

		AND SUBJECT_CODE = #{subjectCode}


	AND STUDENT_CODE = #{studentCode}


	AND TEST_DATE = #{testDate}
